import { useEffect, useState } from "react";
import "./App.css";
import { Container, AppBar, Typography, Grow, Grid } from "@material-ui/core";

import { useDispatch } from 'react-redux'; // allows us to dispatch redux actions

import memories from './images/memories.png';
import Posts from './components/Posts/Posts';
import Form from './components/Form/Form';
import useStyles from './styles'

import { getPosts } from './actions/posts'

function App() {
  const [currentId, setCurrentId] = useState(null); //**  it's null at the start because we don't have the id selected */
  const classes = useStyles();
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getPosts());
  }, [dispatch]);

  return (
    <Container maxWidth="lg">
      <AppBar className={classes.appBar } position="static" color="inherit">
        <Typography className={classes.heading } variant='h2' align='center'>Memories</Typography>
        <img className={classes.image } src={memories} alt="memories" height='60' />
      </AppBar>
      <Grow in>
        <Container>
          <Grid container justifyContent='space-between' alignItems='stretch' spacing={3}>
            <Grid item xs={12} sm={7}>
              <Posts setCurrentId={setCurrentId}/> {/* passing the setter method (which is the (setCurrentId) */}
            </Grid>
            <Grid item xs={12} sm={4}>
              <Form currentId={currentId} setCurrentId={setCurrentId} /> {/* passing the current Id as a prop into the form */}
            </Grid>
          </Grid>
        </Container>
      </Grow>
    </Container>
  );
}

export default App;
