// A reducer is a function that accepts the state and action then based on the action type, it carries out a logic

import { FETCH_ALL, FETCH_BY_SEARCH, CREATE, UPDATE , DELETE, START_LOADING, END_LOADING } from '../constants/actionTypes';


// This is where we recreate our actions
export default (state = { isLoading: true, posts: [] }, action) => {
  switch (action.type) {

    //***FOR NORMAL POSTS
    // case FETCH_ALL: //for fetching all post
    //   return action.payload;

    case START_LOADING:
      return { ...state, isLoading: true };
    case END_LOADING:
      return { ...state, isLoading: false };

    //****FOR POSTS WITH PAGES */

    case FETCH_ALL:
      return { 
        ...state,
        posts: action.payload.data,
        currentPage: action.payload.currentPage,
        numberOfPages: action.payload.numberOfPages,
      };
    case CREATE: //for creating new posts
      return {...state, posts:[ ...state.posts, action.payload]}; //the posts would be added to the action.payload
      case START_LOADING:
        return { ...state, isLoading: true };
      case END_LOADING:
        return { ...state, isLoading: false };
    case FETCH_BY_SEARCH:
      return {...state, posts: action.payload }
    case DELETE:
      return {...state, posts:  state.posts.filter((post) => post._id !== action.payload )} // we're returning all the posts, but filtering out the one we deleted
    case UPDATE:
      return {...state, posts: state.posts.map((post) =>
        post._id === action.payload._id ? action.payload : post
      )};

    default:
      return state;
  }
};

//Note 
//For the UPDATE CASE
//*action.payload is the updated post** so if  
//the post id is equal to action.payload id, we want to return the action.payload. Because action.payload is the newly updated post. else we return the post as it was without any updates ***** */
