// This is where all the routes are initiated from the backend to the front end
import axios from 'axios';

const API = axios.create({ baseURL: 'http://localhost:5000' })

// const url = 'http://localhost:5000/posts';


export const fetchPosts = () => API.get('/posts');
export const createPost = (newPost) => API.post('/', newPost);
export const updatePost = (id, updatedPost) => API.patch(`${url}/${id}`, updatedPost);
export const deletePost = (id) => API.delete(`${url}/${id}`);
export const likePost = (id) => API.patch(`${url}/${id}/likePost`);


//once we have the api requests here, we move on to Actions
