//********************************* */
//for fetching single post(post with more details)
export const getPost = (id) => async (dispatch) => {
    try {
        dispatch({ type: START_LOADING })
        const { data } = await api.fetchPost(id);

        dispatch({ type: FETCH_POST, payload: data }); //our payload is now an object that contains different things(data of post, current page and number of pages)
        dispatch({ type: END_LOADING });
    } catch (error ) {
        console.log(error.message); 
    }
};


//for getting posts with pages
export const getPosts = (page) => async (dispatch) => {
    try {
        dispatch({ type: START_LOADING })
        const { data } = await api.fetchPosts(page);

        dispatch({ type: FETCH_ALL, payload: data }); //our payload is now an object that contains different things(data of post, current page and number of pages)
        dispatch({ type: END_LOADING });
    } catch (error ) {
        console.log(error.message); 
    }
};

//^^^^^^^
export const getPostsBySearch = (searchQuery) => async (dispatch) => {
    try {
        dispatch({ type: START_LOADING })
        const { data: { data } } = await api.fetchPostsBySearch(searchQuery);

        dispatch({ type: FETCH_BY_SEARCH, payload: data });
        dispatch({ type: END_LOADING });
    } catch (error) {
        console.log(error)
    }

};
//^^^^^



export const createPost = (post, navigate ) => async (dispatch) => {
    try {
        dispatch({ type: START_LOADING })
        const { data } = await api.createPost(post); // this is simply making an api request to the back end server

        navigate(`/posts/${data._id}`); //Renavigate to the post details of the newly created post ***optional 

        dispatch({ type: CREATE, payload: data  });
        dispatch({ type: END_LOADING });
    } catch (error ) {
        console.log(error); 
    }
};

// ****
export const updatePost = (id, post) => async (dispatch) => {
    try {
        const { data } = await api.updatePost(id, post); 

        dispatch({ type: UPDATE, payload: data  });
    } catch (error ) {
        console.log(error ); 
    }
};

export const deletePost = (id) => async (dispatch) => {
    try {
        await api.deletePost(id); 

        dispatch({ type: DELETE, payload: id  });
    } catch (error ) {
        console.log(error); 
    }
};

export const likePost = (id) => async (dispatch) => {
    // const user = JSON.parse(localStorage.getItem('profile'));  //***** */ 
    try {
        const { data } = await api.likePost(id); 

        dispatch({ type: UPDATE, payload: data  }); //******* */
    } catch (error ) {
        console.log(error); 
    }
};


