import { AUTH, LOGOUT } from "../constants/actionTypes";

const authReducer = (state = { authData : null }, action) => {
  switch (action.type) {
    case AUTH:
      localStorage.setItem('profile',) //saving the data 
      return state;

    default:
      return state;
  }
};

export default authReducer;

