import { useEffect, useState } from "react";
import "./App.css";
import { Container, Grow, Grid } from "@material-ui/core";

import { useDispatch } from 'react-redux'; // allows us to dispatch redux actions

import Posts from './components/Posts/Posts';
import Form from './components/Form/Form';
import useStyles from './styles'

import { getPosts } from './actions/posts'
import Navbar from "./components/Navbar/Navbar"; //*/*//

function App() {
  const [currentId, setCurrentId] = useState(null); //**  it's null at the start because we don't have the id selected */
  const classes = useStyles();
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getPosts());
  }, [currentId, dispatch]); //adding currentId as a dependency would mean we don't have to refresh everytime we make changes

  return (
    <Container maxWidth="lg">
      <Navbar/>
      <Grow in>
        <Container>
          <Grid container className={classes.mainContainer} justifyContent='space-between' alignItems='stretch' spacing={3}>
            <Grid item xs={12} sm={7}>
              <Posts setCurrentId={setCurrentId}/> {/* passing the setter method (Methods which update a variable) which is the (setCurrentId) as props into the Posts component */}
            </Grid>
            <Grid item xs={12} sm={4}>
              <Form currentId={currentId} setCurrentId={setCurrentId} /> {/* passing the current Id as a prop into the form component. a setter method will be passed here as well  */}
            </Grid>
          </Grid>
        </Container>
      </Grow>
    </Container>
  );
}

export default App;
