import React, {useEffect} from "react";
import { Pagination, PaginationItem } from '@material-ui/lab';
import useStyles from './styles'; 
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from 'react-redux'; // allows us to dispatch redux actions

import { getPosts } from '../actions/posts'

const Paginate = ({page}) => {
    const classes = useStyles();
    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(getPosts());
    }, [dispatch]); //in this hook, we want to fetch the post anytime the page changes

    return ( 
        <Pagination
            classes={{ ul: classes.ul}}
            count={5} //this is static but we'll have to fetch the number of count depending on the number of pages we have.
            page={1} //This will be dynamic later on
            variant="outlined"
            color="primary"
            renderItem ={(item) => (
                <PaginationItem {...item} component={Link} to={`/posts?page=${1}`}/>
            )}
        />
     );
}

export default Paginate;

// Our current static pagination is now done, we'll import it inside the home component
